DROP TABLE IF EXISTS zones_to_post_code_it;
CREATE TABLE IF NOT EXISTS zones_to_post_code_it (
  post_zone_id int NOT NULL AUTO_INCREMENT,
  zone_country_id int NOT NULL,
  zone_id int NOT NULL DEFAULT '0',
  post_code varchar(32) NOT NULL,
  zone_name varchar(64) NOT NULL,
  zone_city_name varchar(64) NOT NULL,
  PRIMARY KEY (post_zone_id),
  UNIQUE KEY post_code (post_code,zone_name,zone_city_name),
  KEY zone_country_id (zone_country_id),
  KEY post_code_2 (post_code),
  KEY zone_id (zone_id)
) ENGINE=MyISAM;

